function GetManjaActionIDsAvailableToEntityType( $entity_type_id ) {
	switch( $entity_type_id ) {
	case ALET_NONE:				return array(ALA_SEARCH);
	case ALET_MEDIA:			return array(ALA_MEDIA_DOWNLOAD,ALA_MEDIA_UPLOAD,ALA_MEDIA_DELETE,ALA_MEDIA_RESTORE,ALA_MEDIA_EDIT_META,ALA_MEDIA_ASSIGN_CATEGORY,   ALA_MEDIA_CUSTOM_PREVIEW_UPLOAD,ALA_MEDIA_CUSTOM_PREVIEW_REMOVE,   ALA_MEDIA_EXPORT,   ALA_MEDIA_VERSION_DOWNLOAD,ALA_MEDIA_VERSION_PROMOTE, ALA_MEDIA_XMP_UPLOAD);
	case ALET_INDEX_LIST:		return array(ALA_INDEX_LIST_FILTER,ALA_INDEX_LIST_CREATE,ALA_INDEX_LIST_DELETE,ALA_INDEX_LIST_SEND_USER,ALA_INDEX_LIST_SEND_GUEST,ALA_INDEX_LIST_RENAME,ALA_INDEX_LIST_NOTE,ALA_INDEX_LIST_MEDIA_ADD,ALA_INDEX_LIST_MEDIA_REMOVE);
	case ALET_CLIENT:			return array(ALA_CLIENT_DATA_SET,ALA_CLIENT_DATA_REMOVE);
	case ALET_TREE:				return array(ALA_TREE_ADD,ALA_TREE_UPDATE,ALA_TREE_DELETE);
	case ALET_CATEGORY:			return array(ALA_CATEGORY_ADD,ALA_CATEGORY_UPDATE,ALA_CATEGORY_DELETE,ALA_CATEGORY_MOVE,   ALA_CATEGORY_SORT,ALA_CATEGORY_CUSTOM_PREVIEW_UPLOAD,ALA_CATEGORY_CUSTOM_PREVIEW_REMOVE);
	case ALET_USER_GROUP:		return array(ALA_USER_GROUP_ADD,ALA_USER_GROUP_UPDATE,ALA_USER_GROUP_DELETE);
	case ALET_USER:				return array(ALA_LOGIN,ALA_SESSION_CREATE,ALA_LOGOUT,   ALA_USER_ADD,ALA_USER_UPDATE,ALA_USER_PASSWORD,ALA_USER_DELETE, ALA_LOGIN_FAILED);
	case ALET_ACL_ITEM:			return array(ALA_ACL_ITEM_ADD,ALA_ACL_ITEM_UPDATE,ALA_ACL_ITEM_DELETE,ALA_ACL_ITEM_MOVE);
	case ALET_ACL:				return array(ALA_ACL_DELETE,ALA_ACL_COPY);
	case ALET_ARCHIVE:			return array(ALA_ARCHIVE_CREATE,ALA_ARCHIVE_GET);
	case ALET_MAINTENANCE:		return array(ALA_MAINTENANCE);
	case ALET_DOWNLOAD_FORMAT:	return array(ALA_DLF_ADD,ALA_DLF_UPDATE,ALA_DLF_DELETE);
	case ALET_PREVIEW_FORMAT:	return array(ALA_PF_ADD,ALA_PF_UPDATE,ALA_PF_DELETE);
	case ALET_META_GROUP:		return array(ALA_MG_ADD,ALA_MG_UPDATE,ALA_MG_DELETE,ALA_MG_MOVE);
	case ALET_META_DEFINITION:	return array(ALA_MD_ADD,ALA_MD_UPDATE,ALA_MD_DELETE,ALA_MD_MOVE);
	case ALET_EXPORT_CHANNEL:	return array(ALA_EXPORT_CHANNEL_ADD,ALA_EXPORT_CHANNEL_UPDATE,ALA_EXPORT_CHANNEL_DELETE);
	}
	return array();
}



